import React, { useEffect, useState } from "react";
import { connect, useDispatch} from "react-redux"; 
// import {withRouter} from "react-router-dom";
import { actionCreators } from "../actions/profile";


const RightContent =({adduser}) =>{ 
    const [inpval , setInpval] = useState({
    name: "",
    email: "",
    userName: "",
    mobile: "",
     city: "",
    password : "",
    confirmPassword :""
    });

const handleClick =(e) =>{
  e.preventDefault();
  let newUser = {
    name :inpval.name,
    email : inpval.email,
    userName : inpval.userName, 
    mobile: inpval.mobile,
   city : inpval.city,
    password : inpval.password,
    confirmPassword : inpval.confirmPassword
  }

  adduser(newUser);
  // setInpval((prev)=>({...prev , firstName :'' , lastName :'', telephone : '' , age :'' , state : '', email : '', password :''}))

  const {name, userName , mobile , email ,city, confirmPassword , password} = inpval;
  if(name==='')
  {
    alert("please fill this first name field");
  }
  else if(userName==='')
  alert("please fill this last name field");
  else if(mobile==="")
  alert("please fill this required field");

  else if(!email.includes("@"))
  alert("please enter valid email format");
  else if(city=='')
  alert("required this field");

  else if(password==="")
  alert("please fill this password field");

  else if(confirmPassword===password && confirmPassword!=="")
  alert("Doesn't match your password . please check again");

}



      return (
        <div>
            <form>
          <h1>Registration Form </h1>
          <label htmlFor="name" className="label">Name : </label>
          <div className="container">
          <input type ="text" placeholder="Enter First Name"   value ={inpval.name} className="form-control" onChange={((e)=>setInpval((prev)=>({...prev,name:e.target.value})))}></input>

          <label htmlFor="email" className="label"> Email: </label>
          <input type = "email" placeholder="Enter email" className="form-control"    value ={inpval.email}  onChange={((e)=>setInpval((prev)=>({...prev,email:e.target.value})))}></input>

          <label htmlFor="lastname" className="label">User Name : </label>
          <input type ="text" placeholder="Enter Last Name"   className="form-control" value ={inpval.userName}onChange={((e)=>setInpval((prev)=>({...prev,userName:e.target.value})))}></input>

          <label htmlFor="phone" className="label">Mobile : </label>
          <input type ="text" placeholder="Enter phone no."   value ={inpval.mobile}   className="form-control" onChange={((e)=>setInpval((prev)=>({...prev,mobile:e.target.value})))}></input>

          <label htmlFor="age" className="label">City: </label>
          <input type ="text" placeholder="Enter age"   className="form-control"  value ={inpval.city}     onChange={((e)=>setInpval((prev)=>({...prev,city:e.target.value})))}></input>


          <label htmlFor="state" className="label">Password : </label>
          <input type ="text" placeholder="Enter state"  className="form-control"  value ={inpval.password}  onChange={((e)=>setInpval((prev)=>({...prev,password:e.target.value})))}></input>

          <label htmlFor="password" className="label">Confirm Password : </label>
          <input type ="password" placeholder="Enter password"  className="form-control"  value ={inpval.confirmPassword} onChange={((e)=>setInpval((prev)=>({...prev,confirmPassword:e.target.value})))}></input>

          <br></br>
          <button className="btn btn-primary" onClick={handleClick}>Register</button>
          </div>
          </form>
        </div>
      )

}

const mapDispatchToProps = (dispatch) => {
  return {
    adduser: (data) => dispatch(actionCreators.addProfile(data)),
  }
}

const LoginContainer = connect(null, mapDispatchToProps)(RightContent)

export default LoginContainer
