import { LOGIN_USER, REGISTER_USER} from "../constants/actionTypes";

const initialState = {
  profile: {
    name: "",
    username: "",
    email: "",
    mobile: "",
    city: "",
    password: "",

  },
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_USER:
     let data = action.user
     console.log("Reducer Data ===>", data);
      return {
        ...state,
        profile: data,
      };

      case LOGIN_USER:
        let data2 ={
          email :'',
          password :''
        }
        let loginData = action.user
        console.log("fghfgfg===>",loginData);
        return{
          ...state,
          data2 : loginData,
        }
    default:
      return state;
  }
};

export default reducer;
