import { REGISTER_USER , LOGIN_USER} from "../constants/actionTypes";
import { API } from "../api/api";
import axios from "axios";
import { config } from "../api/config";

export const actionCreators = () =>{
   console.log("actionCreators");
return dispatch =>{
  console.log("actionCreators dispatch");

  axios.get(`http://139.162.63.46:4012/users`)
  .then(res=>{
    const persons = res.data;

    dispatch({
      type : REGISTER_USER,
      users : res
    });
  })
}

};


export const AddUser = (params) => {
  console.log("AddUser====>>>", params);

  return dispatch => {
      console.log("Add User dispatch");

      axios.post(config.REGISTER_USER, params)
      .then(response => {
          console.log("response=========>>>c", response);

          dispatch({
            type: REGISTER_USER,
            users: response
        });
      })
  };
};
