import React, { useEffect, useState } from "react";
import { connect, useDispatch} from "react-redux"; 
// import {withRouter} from "react-router-dom";
import { actionCreators } from "../actions/profile";


const RightContent =({adduser}) =>{ 
    const [inpval , setInpval] = useState({
    firstName: "",
    lastName: "",
    telephone: "",
    age: "",
    email: "",
    state: "",
    password : ""

    });

const handleClick =(e) =>{
  e.preventDefault();
  let newUser = {
    firstName :inpval.firstName,
    lastName : inpval.lastName,
    telephone: inpval.telephone,
    email : inpval.email,
    age : inpval.age,
    state : inpval.state,
    password : inpval.password
  }
  adduser(newUser);
  setInpval((prev)=>({...prev , firstName :'' , lastName :'', telephone : '' , age :'' , state : '', email : '', password :''}))



}

const {firstName} = inpval

      return (
        <div>
            <form>
          <h1>Registration Form </h1>
          <label htmlFor="firstname" className="label">First Name : </label>
          <div className="container">
          <input type ="text" placeholder="Enter First Name"   value ={inpval.firstName} className="form-control" onChange={((e)=>setInpval((prev)=>({...prev,firstName:e.target.value})))}></input>

          <label htmlFor="lastname" className="label">Last Name : </label>
          <input type ="text" placeholder="Enter Last Name"   className="form-control" value ={inpval.lastName}onChange={((e)=>setInpval((prev)=>({...prev,lastName:e.target.value})))}></input>

          <label htmlFor="phone" className="label">Telephone : </label>
          <input type ="text" placeholder="Enter phone no."   value ={inpval.telephone}   className="form-control" onChange={((e)=>setInpval((prev)=>({...prev,telephone:e.target.value})))}></input>

          <label htmlFor="age" className="label">Age: </label>
          <input type ="text" placeholder="Enter age"   className="form-control"  value ={inpval.age}     onChange={((e)=>setInpval((prev)=>({...prev,age:e.target.value})))}></input>

          <label htmlFor="email" className="label"> Email: </label>
          <input type = "email" placeholder="Enter email" className="form-control"    value ={inpval.email}  onChange={((e)=>setInpval((prev)=>({...prev,email:e.target.value})))}></input>

          <label htmlFor="state" className="label">State : </label>
          <input type ="text" placeholder="Enter state"  className="form-control"  value ={inpval.state}  onChange={((e)=>setInpval((prev)=>({...prev,state:e.target.value})))}></input>

          <label htmlFor="password" className="label">Password : </label>
          <input type ="password" placeholder="Enter password"  className="form-control"  value ={inpval.password} onChange={((e)=>setInpval((prev)=>({...prev,password:e.target.value})))}></input>

          <br></br>
          <button className="btn btn-primary" onClick={handleClick}>Register</button>
          </div>
          </form>
        </div>
      )

}

const mapDispatchToProps = (dispatch) => {
  return {
    adduser: (data) => dispatch(actionCreators.addProfile(data)),
  }
}

const LoginContainer = connect(null, mapDispatchToProps)(RightContent)

export default LoginContainer
