import { LOGIN_USER, REGISTER_USER, USER_PROFILE , EDIT_USER} from "../constants/actionTypes";

const initialState = {
  profile: {
    name: "",
    username: "",
    email: "",
    mobile: "",
    city: "",
    password: "",
  },
  user: {},
  editUser :{},
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_USER:
     let data = action.users
     console.log(" Register Reducer Data ===>", data);
      return {
        ...state,
        profile: data,
      };

      case LOGIN_USER:

        let loginData = action.users
        console.log("login reducer data===>",loginData);
        return{
          ...state,
          profile : loginData,
        }

        case USER_PROFILE:

        let userData = action.users
        console.log("userData in user profile reducer===>",userData);
        return{
          ...state,
          user : userData,
        }

        case EDIT_USER:
          let editData = action.users
          console.log("edited dataa in ===>>>>>", editData);
          return{
            ...state,
            editUser: editData,
          }

    default:
      return state;
  }
};

export default reducer;
