// }
const handleClick =(e) =>{
  e.preventDefault();
  let newUser = {
    firstName :inpval.firstName,
    lastName : inpval.lastName,
    telephone: inpval.telephone,
    email : inpval.email,
    age : inpval.age,
    state : inpval.state
  }
  adduser(newUser);
}


      return (
        <div>
            <form>
          <h1>Registration Form </h1>
          <label htmlFor="firstname">First Name : </label>
          <div className="container">
          <input type ="text" placeholder="Enter First Name" name="fname" className="form-control" onChange={((e)=>setInpval((prev)=>({...prev,firstName:e.target.value})))}></input>
 <br></br> <br></br></div>
          <label htmlFor="lastname">Last Name : </label>
          <input type ="text" placeholder="Enter Last Name"  onChange={((e)=>setInpval((prev)=>({...prev,lastName:e.target.value})))}></input>
          <br></br> <br></br>
          <label htmlFor="phone">Telephone : </label>
          <input type ="text" placeholder="Enter phone no."    onChange={((e)=>setInpval((prev)=>({...prev,telephone:e.target.value})))}></input>
          <br></br> <br></br>
          <label htmlFor="age">Age: </label>
          <input type ="number" placeholder="Enter age"   onChange={((e)=>setInpval((prev)=>({...prev,age:e.target.value})))}></input>
          <br></br> <br></br>
          <label htmlFor="email"> Email: </label>
          <input type ="email" placeholder="Enter email"   onChange={((e)=>setInpval((prev)=>({...prev,email:e.target.value})))}></input>
          <br></br> <br></br>
          <label htmlFor="phone">State : </label>
          <input type ="text" placeholder="Enter state"    onChange={((e)=>setInpval((prev)=>({...prev,state:e.target.value})))}></input>
          <br></br>
          <br></br>
          <button className="btn btn-primary" onClick={handleClick}>Register</button>
          </form>
        </div>
      )

}

const mapDispatchToProps = (dispatch) => {
  return {
    adduser: (data) => dispatch(actionCreators.addProfile(data)),
  }
}

const LoginContainer = connect(null, mapDispatchToProps)(RightContent)

export default LoginContainer
