import React, { useState } from "react";
import { connect } from "react-redux";
import { AddUser, LoginUser } from "../actions/user";
import { REGEX } from "../utils/Regex";
const Login =({loginDetail}) =>{
//  const navigate = useNavigate();
    const[data, setData] = useState({
        email :'',
        password :''
    })

    const handleSubmit =(e) =>{
        e.preventDefault();
   const {email , password} = data;
 if(!REGEX.email.test(email))
 alert("enter valid email");
 else if (password == "") alert("please fill this password field");
    else if (!password.match(/[A-Z]/))
      alert("please enter at least one uppercase letter");
    else if (!password.match(/[a-z]/))
      alert("please enter at least one lowercase letter");
    else if (password.length != 8) alert("please enter At least 8 characters");
    else if (!password.match(/[\d`~!@#$%\^&*()+=|;:'",.<>\/?\\\-]/))
    alert("please enter At least 1 number or special character");
    else{
        let details ={
            email : data.email,
            password : data.password
        }
        // console.log(details);
        loginDetail(details);
    }
    }


    // const handleClick =() =>{
    //     navigate("/Register");
    // }

  return(
    <div> 
        <h1>Login Page</h1> 
        <hr></hr>
        <div className="container">
         <label htmlFor="email" className="label"> Email: </label>
          <input type = "email" placeholder="Enter email" className="form-control"   autoComplete="off"  onChange={((e)=>setData((prev)=>({...prev,email:e.target.value})))}></input>

          <label htmlFor="password" className="label">Password : </label>
          <input type ="password" placeholder="Enter password"  className="form-control" autoComplete="off"   onChange={((e)=>setData((prev)=>({...prev,state:e.target.value})))}></input>
          </div><br></br>
          <button onClick={handleSubmit} className = "btn btn-primary">Submit</button>
          {/* <p>If you are new user then click on this button</p> */}
           {/* <button onClick={ handleClick}>Register</button> */}

    </div>
  )

}

const mapDispatchToProps =(dispatch)=>{
    return{
        loginDetail : (loginData) =>dispatch(LoginUser(loginData)),
    }



}
const loginStore = connect(null , mapDispatchToProps)(Login)
export default loginStore
