   mobile: "",
    city: "",
   password : '',
   confirmPassword : ''
  },
  formSubmitted: false,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case Types.LOGIN:
     let data2 = action.payload
     console.log("djhfj===>", data2);
      return {
        ...state,
        profile: data2,
